import{_ as t,c as a,a0 as r,o}from"./chunks/framework.CSihnngI.js";const f=JSON.parse('{"title":"网络","description":"","frontmatter":{},"headers":[],"relativePath":"subject/offer/network.md","filePath":"subject/offer/network.md","lastUpdated":1751166161000}'),l={name:"subject/offer/network.md"};function i(n,e,s,p,_,c){return o(),a("div",null,e[0]||(e[0]=[r('<h1 id="网络" tabindex="-1">网络 <a class="header-anchor" href="#网络" aria-label="Permalink to &quot;网络&quot;">​</a></h1><h2 id="说说网络的五层模型" tabindex="-1">说说网络的五层模型 <a class="header-anchor" href="#说说网络的五层模型" aria-label="Permalink to &quot;说说网络的五层模型&quot;">​</a></h2><p>网络模型从上到下依次为应用层、传输层、网络层、数据链路层、物理层。在发送消息时，从上倒下层层打包，每一层在上一层的基础上家暴，而接受消息时，从下到上进行解包，最终得到原始信息。</p><ul><li>应用层：主要面向互联网中的应用场景，比如网页、邮件、文件中心等等，代表的协议有htpp、smtp、ftp、pop3、ftp、dns</li><li>传输层：主要面向传输过程，比如TCP协议是为了保证可靠的传输，而UDP协议则是一种无连接的广播，他们提供了不同的传输方式</li><li>网络层：主要解决如何定位目标以及如何寻找最优路径的问题，比如IP等</li><li>数据链路层：主解决的是如何在子网内有效传输，MAC地址、交换机都属于该层</li><li>物理层：主要处理二进制数据与信号的转换问题，集线器、双绞线、同轴电缆都属于该层的设备</li></ul>',4)]))}const u=t(l,[["render",i]]);export{f as __pageData,u as default};
